import React, { useState } from 'react';

import { Carousel, Card } from 'react-bootstrap';

function Carrousel() {
    const [index, setIndex] = useState(0);

    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };

    return (
        <Carousel activeIndex={index} onSelect={handleSelect} id="carousel">
            <Carousel.Item>
                <img className="d-block w-100" src="/img/cars/car-1.jpg" alt="Looping car" />

                <Carousel.Caption>
                    <h1>Vroom Vroom!!!</h1>
                    <p>Je mets la gomme car j'adore le chewing gum!</p>
                </Carousel.Caption>
            </Carousel.Item>

            <Carousel.Item>
                <img className="d-block w-100" src="/img/cars/bike-1.jpg" alt="Looping car" />

                <Carousel.Caption>
                    <h1>velos 8€/j</h1>
                    <p>Pas de caution. Location de casque gratuite</p>
                </Carousel.Caption>
            </Carousel.Item>

            <Carousel.Item>
                <img className="d-block w-100" src="/img/cars/camionnette-1.jpg" alt="Looping car" />

                <Carousel.Caption>
                    <h1>Dis camion</h1>
                    <p>Tu cherches une belle cylindrée dans ta région?</p>
                </Carousel.Caption>
            </Carousel.Item>

            <Carousel.Item>
                <img className="d-block w-100" src="/img/cars/semi-remorque-1.jpg" alt="Looping car" />

                <Carousel.Caption>
                    <h1>Dis camion</h1>
                    <p>Tu cherches une belle cylindrée dans ta région?</p>
                </Carousel.Caption>
            </Carousel.Item>
        </Carousel>
    );
}

export default Carrousel;
